package ru.isantur.santurshopimport android.content.Contextimport android.webkit.JavascriptInterfaceimport android.widget.Toastclass JavaScriptInterface internal constructor(c: Context) {    var mContext: Context    @JavascriptInterface    fun showToast(toast: String?) {        Toast.makeText(mContext, toast, Toast.LENGTH_SHORT).show()    }    init {        mContext = c    }}