package ru.isantur.santurshopimport android.content.Contextimport android.view.Viewimport android.view.inputmethod.InputMethodManagerimport androidx.core.content.ContextCompat.getSystemServiceobject Utils {    fun hideKeyboard(context: Context, view: View) {        try {            val imm = context.getSystemService(Context.INPUT_METHOD_SERVICE) as InputMethodManager            imm.hideSoftInputFromWindow(view.windowToken, 0)        } catch (e: Exception) {            e.printStackTrace()        }    }    fun showKeyboard(context: Context) {        try {            val imm = context.getSystemService(Context.INPUT_METHOD_SERVICE) as InputMethodManager            imm.toggleSoftInput(InputMethodManager.SHOW_FORCED, 0)        } catch (e: Exception) {            e.printStackTrace()        }    }}